    public void init() {
        //backgroundColor.setColor(Color.BLACK);
        patientData.setColor(Color.RED);
        patientDataRePaint.setColor(Color.WHITE);
        patientData.setStrokeWidth(2);
    }

    @Override
    public synchronized void onDraw(Canvas canvas) {
        super.onDraw(canvas);
        if (!mIsInited) {
            int w = getWidth(), h = getHeight();
            Bitmap.Config conf = Bitmap.Config.ARGB_8888;
            mBitmap = Bitmap.createBitmap(w, h, conf);
            mCanvas = new Canvas(mBitmap);
            mCanvas.drawColor(Color.WHITE);
            mIsInited = true;
            mCanvas.drawLine(0, (getHeight() / 2), w, (getHeight() / 2), patientData);
        }
        if (i != 0) {
            if (mWidthIterator > getWidth()) {
                mWidthIterator = 0;
            } else {
                mWidthIterator += 1;
            }
            mCanvas.drawRect(mWidthIterator, 0, mWidthIterator + 7, getHeight(), patientDataRePaint);
            Log.e("HVV1312","loop : "+i+  "and mWidth = " + mWidthIterator +"    value  "+a[i]+ " anÄ‘ " + a[i-1] +   " length a[]= "+a.length+"   height " + getHeight());
            mCanvas.drawLine(mWidthIterator, (getHeight() / 2) - (a[i]), mWidthIterator + 1, (getHeight() / 2) - (a[i - 1]), patientData);
        }
        canvas.drawBitmap(mBitmap, 0, 0, null);
        i++;
        if (i == a.length) {
            i = 1;
            mWidthIterator = 0;
            mCanvas.drawColor(Color.WHITE);
            mCanvas.drawLine(0, (getHeight() / 2), getWidth(), (getHeight() / 2), patientData);
        }
        invalidate();
    }
